export const QuizData = [
  {
    question: "What is React?",
    options: [
      "1. A JavaScript library for building user interfaces.",
      "2. A programming language.",
      "3. A database management system.",
      "4. A text editor.",
    ],
    answer: 1,
  },
  {
    question: "In React, which method is used to change the state of a component?",
    options: ["1. setState()", "2. changeState()", "3. modifyState()", "4. updateState()"],
    answer: 1,
  },
  {
    question: "What is JSX in React?",
    options: [
      "1. JavaScript eXtensible Syntax",
      "2. JavaScript XML",
      "3. JavaScript Framework",
      "4. JavaScript Style Extension",
    ],
    answer: 2,
  },
  {
    question: "What is the virtual DOM in React used for?",
    options: [
      "1. To improve the performance of DOM updates.",
      "2. To create virtual 3D models.",
      "3. To manage server-side rendering.",
      "4. To handle network requests.",
    ],
    answer: 1,
  },
  {
    question: "What is JavaScript primarily used for in web development?",
    options: [
      "1. Styling web pages.",
      "2. Adding interactivity to web pages.",
      "3. Managing databases.",
      "4. Creating web layouts.",
    ],
    answer: 2,
  },
  {
    question: "What does the typeof operator in JavaScript return for an array?",
    options: ["1. array", "2. object", "3. list", "4. collection"],
    answer: 2,
  },
  {
    question: "How do you declare a variable in JavaScript?",
    options: [
      "1. var myVariable;",
      "2. let myVariable;",
      "3. const myVariable;",
      "4. All of the above.",
    ],
    answer: 4,
  },
  {
    question: "Which function is used to print output to the console in JavaScript?",
    options: ["1. print()", "2. log()", "3. write()", "4. display()"],
    answer: 2,
  },
  {
    question: "What does HTTP stand for in the context of web computing?",
    options: [
      "1. HyperText Transfer Protocol",
      "2. High-Level Transfer Protocol",
      "3. Hypertext Text Protocol",
      "4. HyperTransfer Text Protocol",
    ],
    answer: 1,
  },
  {
    question: "What does HTML stand for?",
    options: [
      "1. HyperText Markup Language",
      "2. Hyper Transfer Markup Language",
      "3. High-Level Text Markup Language",
      "4. Hyperlink and Text Markup Language",
    ],
    answer: 1,
  },
  {
    question: "Which HTML tag is used to create a hyperlink?",
    options: ["1. <link>", "2. <url>", "3. <a>", "4. <href>"],
    answer: 3,
  },
  {
    question: "What does CSS stand for?",
    options: [
      "1. Cascading Style Sheets",
      "2. Computer Style Sheets",
      "3. Colorful Style Sheets",
      "4. Creative Style Sheets",
    ],
    answer: 1,
  },
  {
    question: "Which CSS property is used to change the text color of an element?",
    options: ["1. background-color", "2. font-color", "3. text-color", "4. color"],
    answer: 4,
  },
];
